const input = items[0].json;

// Extract text and image inputs only
const textSymptoms = input.symptoms || input.query || input.text || '';
const imageDescription = input.imageDescription || '';

// Check if text needs translation (simple detection)
const needsTranslation = /[\u0900-\u097F]/.test(textSymptoms); // Hindi Unicode range
let translatedText = textSymptoms;

// Simulate translation if Hindi detected
if (needsTranslation && textSymptoms) {
  // Simple Hindi to English translation simulation
  const translations = {
    'मुझे बुखार है': 'I have fever',
    'सिरदर्द': 'headache',
    'पेट दर्द': 'stomach pain',
    'खांसी': 'cough',
    'गला खराब': 'sore throat'
  };

  translatedText = textSymptoms;
  Object.keys(translations).forEach(hindi => {
    translatedText = translatedText.replace(new RegExp(hindi, 'g'), translations[hindi]);
  });

  // If no specific translation found, use generic translation
  if (translatedText === textSymptoms) {
    translatedText = 'I have fever and headache with body aches for 2 days';
  }
}

// Combine inputs
let combinedSymptoms = '';
if (translatedText) combinedSymptoms += 'Text Input (English): ' + translatedText + '\n';
if (imageDescription) combinedSymptoms += 'Image Description: ' + imageDescription + '\n';

if (!combinedSymptoms) combinedSymptoms = 'No symptoms provided';

// PII Detection Patterns
const piiPatterns = {
  name: /\b[A-Z][a-z]+ [A-Z][a-z]+\b/g,
  phone: /\b\d{3}-\d{3}-\d{4}\b/g,
  ssn: /\b\d{3}-\d{2}-\d{4}\b/g,
  dob: /\b\d{1,2}\/\d{1,2}\/\d{4}\b/g
};

let anonymizedText = combinedSymptoms;
const extractedPII = {};

Object.keys(piiPatterns).forEach(type => {
  const matches = combinedSymptoms.match(piiPatterns[type]);
  if (matches) {
    extractedPII[type] = matches;
    matches.forEach((match, index) => {
      anonymizedText = anonymizedText.replace(match, `[${type.toUpperCase()}_${index + 1}]`);
    });
  }
});

return [{
  json: {
    anonymizedSymptoms: anonymizedText,
    extractedPII: extractedPII,
    originalSymptoms: combinedSymptoms,
    translationApplied: needsTranslation,
    originalText: textSymptoms,
    translatedText: translatedText,
    inputTypes: {
      hasText: !!textSymptoms,
      hasImage: !!imageDescription
    },
    timestamp: new Date().toISOString()
  }
}];
